import React from "react";
import Select from "react-select";
import { getCities, saveHistory } from "../services/cityService";
import { getWeatherMap } from "../services/openWeatherService";
import Map from "./map"
import { useState } from 'react';





class SelectCity extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: [],
      lat: 4.747286,
      lon: -74.114399,
      humidity:"",
      allCities:[],
      showAll:false,
      currentCity:""
    };
  }

  async componentDidMount() {
    const { data } = await getCities();
    const cityOptions = data.map((city) => {
      return { value: city, label: city.name };
    });
    this.setState({
      cities: cityOptions,
      allCities: data
    });
  }

  handleChange = async (selectedCity) => {
    let city = selectedCity.value
    this.setState({
      lat: city.lat,
      lon: city.lon,
      humidity: city.humidity,
      showAll: false,
      currentCity:city

    });
  };

  showAllCities = ()=>{
    this.setState({
      showAll: true
    });
  }

  saveHistory = ()=>{
    let params = {}
    if (this.state.showAll==false){
      params['records']=[this.state.currentCity]
    }else{
      const citiesParams = this.state.allCities.map((city) => {
        return { city_id: city.id, humidity: city.humidity };
      });
      params['records']=citiesParams
    }
  }

  render() {
    const { cities } = this.state;

    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-3">
            <div className="row">
              <div className="col-md-12">
                <label>Seleccione la ciudad</label>
                <Select options={cities} onChange={this.handleChange} />
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <button  type="button" className="btn btn-primary" onClick={this.showAllCities}>Ver todas las ciudades</button>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <div className="row">
              <div className="col-md-12">
                <Map 
                lat={this.state.lat} 
                lon={this.state.lon}
                humidity={this.state.humidity}
                cities = {this.state.allCities}
                showAll = {this.state.showAll}
                />
              </div>

            </div>
            <div className="row">
              <div className="col-md-12">
                <button  type="button" className="btn btn-success" onClick={this.saveHistory}>Guardar historial</button>
              </div>

            </div>
          </div>
          <div className="col-md-4">

          </div>
        </div>
      </div>
    );
  }
}

export default SelectCity;
