import React from "react";
import Select from "react-select";
import { getCities, getOneCity } from "../services/cityService";
import { getWeatherMap } from "../services/openWeatherService";
import Map from "./map"
import { useState } from 'react';





class SelectCity extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cities: [],
      lat: 4.747286,
      lon: -74.114399
    };
  }

  async componentDidMount() {
    const { data } = await getCities();
    const cityOptions = data.map((city) => {
      return { value: city, label: city.name };
    });
    this.setState({
      cities: cityOptions,
    });
  }

  handleChange = async (selectedCity) => {
    console.log("toacity",selectedCity.value)
    city = selectedCity.value
    /*const { data } = await getOneCity(selectedCity.value);
    this.setState({
      lat: data.lat,
      lon: data.lon
    });*/
    // getWeatherMap(data.lat, data.lon).then(({ data }) => {
    //   console.log(data);
    //   this.setState({
    //     humidity: data.current.humidity,
    //   });
    // });
  };

  render() {
    const { cities } = this.state;

    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-3">
            <div className="row">
              <div className="col-md-12">
                <label>Seleccione la ciudad</label>
                <Select options={cities} onChange={this.handleChange} />
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <button  type="button" className="btn btn-primary">Ver todas las ciudades</button>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            La humedad es: {this.state.humidity}
            <Map lat={this.state.lat} lon={this.state.lon}/>
          </div>
          <div className="col-md-4">

          </div>
        </div>
      </div>
    );
  }
}

export default SelectCity;
